import logo from './images/panda.png';
import './css/App.css';

// Components
import MySubsection from './components/MySubsection';
import SectionCard from './components/SectionCard'
import React from 'react';

function helloWorld(pName, pCountry){
  return (
    <React.Fragment>
      <h2>Hello I'm {pName}, it's a pleasure</h2>
      <h3>I'm from {pCountry}</h3>
    </React.Fragment>
  );
}

const blogArray = [
  {
    title: 'Chips',
    description: 'You will learn about preparing chips fried',
    theme: 'chips'
  },
  {
    title: 'Salad',
    description: 'I can tell you a bit about cooking a salad',
    theme: 'salad'
  }
]

//React app

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />

        <h1 style={
          {
            margin: '0 10px',
            borderRadius: '5px',
            boxShadow: '0 2px 5px #ccc'
          }
        }>
          This is my little React app!
        </h1>
        {helloWorld('Chef Mr Panda', 'Argentina')}

      </header>

      <div className='container'>
        <section className="sections">
          <h1>Sections</h1>
          {blogArray.map((item, pos) => {
            return (
              <SectionCard key={pos} title={item.title} description={item.description} theme={item.theme} />
            )
          })}
        </section>
        <section className="subsections">
          <h1>Subsections</h1>
          {blogArray.map((item, pos) => {
            return (
              <MySubsection id={item.theme} title={item.title}/>
            )
          })}
        </section>
      </div>
    </div>
  );
}

export default App;
