import React from 'react';


class MyComponent extends React.Component {
  render(){
    const recipe = {
      name: 'salad',
      ingredients: ['tomato', 'lettuce', 'beans', 'oil', 'salt']
    }

    return (
      <React.Fragment> {/* <React.Fragment> is to wrap many JSX tags without creating new tags */}
        <h1>Cooking Recipe</h1>
        <h2>I'm a component YEAH</h2>
        <p>I have a cooking recipe for you</p>
        <ul>
          {
            recipe.ingredients.map((ingredient, i) => {
              return (
                <li key={i}> {ingredient} </li>
              )
            })
          }
        </ul>
      </React.Fragment>
    )
  }
}

export default MyComponent;
