/* This is a Function-based component */

import React from 'react';
// import './css/SectionCard.css';
import classes from './css/SectionCard.module.css'

// const classCard = 'SectionCard'
const classCard = classes.sectionCard;

class SectionCard extends React.Component {
  state = {
    likesCount: 10
  };

  onLikeButtonClick = (e) => {
    console.log('Like button');
    // e.stopPropagation();
  }
  onAnchorClick = (e) => {
    console.log('Anchor');
    // e.stopPropagation();
  }

  render() {
    return (
      <a className={classCard} href={`#${this.props.theme}`} onClickCapture={this.onAnchorClick}>
        <h3>{this.props.title}</h3>
        <p>{this.props.description}</p> 
        <p>Like Count: {this.state.likesCount}</p>
      <button onClickCapture={this.onLikeButtonClick}> Like </button>
      </a>
    )
  }
}

export default SectionCard;
