import logo from './assets/images/panda.png';
import './assets/css/App.css';

// Components
import MyComponent from './components/MyComponent';
import BlogCard from './components/BlogCard'
import React from 'react';

function helloWorld(pName, pCountry){
  return (
    <React.Fragment>
      <h2>Hello I'm {pName}, it's a pleasure</h2>
      <h3>I'm from {pCountry}</h3>
    </React.Fragment>
  );
}

const blogArray = [
  {
    title: 'Blog1',
    description: 'I want to tell you about Cooking',
    theme: 'cook'
  },
  {
    title: 'Blog2',
    description: 'I want to tell you about Mathematics',
    theme: 'math'
  }
]

//React app

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />

        <h1 style={
          {
            margin: '0 10px',
            borderRadius: '5px',
            boxShadow: '0 2px 5px #ccc'
          }
        }>
          This is my little React app!
        </h1>
        {helloWorld('Silvana', 'Argentina')}

      </header>


      <section className="components">
        {blogArray.map((item, pos) => {
          return (
            <BlogCard key={pos} title={item.title} description={item.description} theme={item.theme} />
          )
        })}

        <MyComponent id="cook" title="Cooking"/>
        <MyComponent id="math" title="Mathematics"/>
      </section>

    </div>
  );
}

export default App;
